declare interface WhichWayPopTipConfig {
  /**
   * 提示的唯一标识符（ID）。
   * 若未提供，系统将自动生成为 `sjzx_${name}` 格式（`name` 为对象键名）。
   * 此 ID 用于内部去重、查找或后续更新/移除提示。
   */
  id?: string;

  /**
   * 提示的显示名称（通常作为标题或关键词）。
   * 若未提供，默认使用注册时的键名（即 `popTips` 的 key）。
   * 注意：该字段主要供调试或日志记录使用，不一定直接显示在 UI 中。
   */
  name?: string;

  /**
   * 提示的核心内容信息。
   * - 若为字符串：视为单行内容；
   * - 若为字符串数组：将按顺序拼接为多行，每行用 `<br>` 分隔。
   * 最终渲染为 HTML 片段，支持基础标签（如 `<br>`、`<font>` 等）。
   */
  info: string | string[];

  /**
   * 控制是否对 `info` 中的文本添加书名号 `『』` 包裹（常用于强调关键信息）。
   * - `true`：仅包裹 `info` 数组的第 0 项（若为字符串，则包裹整个内容）；
   * - `false`：不包裹任何项（⚠️注意：`false` 不影响来源追加逻辑）；
   * - `number`：包裹指定索引的项（如 `2` 表示包裹第 3 行）；
   * - `number[]`：包裹多个指定索引的项（如 `[0, 2]` 表示包裹第 1、3 行）；
   * 未定义（`undefined`）时，默认不包裹。
   */
  trim?: boolean | number | number[];

  /**
   * 提示信息的来源说明，将自动追加在内容末尾，格式为：
   * `<br><font color="red">来自${sourceInfo}</font>`
   * - 若未提供且 `trim !== false`，默认使用 `"驶舰之向"`；
   * - 若显式提供（如 `"战术手册"`），则显示为 `"来自战术手册"`；
   * - 若设为 `""`（空字符串），仍会显示 `"来自"`（不推荐）；
   * 建议提供明确来源以增强信息可信度与可追溯性。
   */
  sourceInfo?: string;

  /**
   * 类型标记，用于区分不同类型的提示。
   */
  type?:"skill" | "card" | "rule"
}